mapper.select:
SELECT app_estate.estate_id, app_estate.estate_name, app_estate.estate_floor, app_estate.estate_code, MAX(app_district.district_name) AS district_name, app_estate.estate_address, app_estate.estate_area_land, app_estate.estate_area_landcertificate, app_estate.estate_area_estate, app_estate.estate_area_estatecertificate, app_yjqy.yjqy_name, app_qsdw.qsdw_name, app_landstatus.landstatus_name, app_estatetype.estatetype_name, app_estatefunction.estatefunction_name, SUM(CASE WHEN app_suite.suite_isselfuse = 0 THEN app_suite.suite_area ELSE NULL END) AS rent_area, SUM(CASE WHEN app_suite.suite_isselfuse = 1 THEN app_suite.suite_area ELSE NULL END) AS selfuse_area, (app_estate.estate_area_estate - SUM(app_suite.suite_area)) AS vacant_area, COUNT(app_suite.suite_id) AS suite_num FROM app_estate LEFT JOIN app_district ON app_estate.estate_district_code = app_district.district_code LEFT JOIN app_yjqy ON app_estate.yjqy_id = app_yjqy.yjqy_id LEFT JOIN app_qsdw ON app_estate.qsdw_id = app_qsdw.qsdw_id LEFT JOIN app_estatefunction ON app_estate.estate_estatefunction_id = app_estatefunction.estatefunction_id LEFT JOIN app_estatetype ON app_estate.estate_estatetype_id = app_estatetype.estatetype_id LEFT JOIN app_landstatus ON app_estate.estate_landstatus_id = app_landstatus.landstatus_id JOIN app_suite ON app_suite.estate_id = app_estate.estate_id
app_estate.estate_id != 0
AND app_estate.estate_name LIKE #{estateNameLike}
AND app_estate.estate_code LIKE #{estateCodeLike}
AND app_district.district_name LIKE #{districtNameLike}
AND app_yjqy.yjqy_name = #{query.yjqyName}
AND app_qsdw.qsdw_name = #{query.qsdwName}
AND app_estatefunction.estatefunction_name = #{query.estatefunctionName}
AND app_estatetype.estatetype_name = #{query.estatetypeName}
AND app_landstatus.landstatus_name = #{query.landstatusName}
GROUP BY app_estate.estate_id, app_estate.estate_name, app_estate.estate_floor, app_estate.estate_code, app_estate.estate_address, app_estate.estate_area_land, app_estate.estate_area_landcertificate, app_estate.estate_area_estate, app_estate.estate_area_estatecertificate, app_yjqy.yjqy_name, app_qsdw.qsdw_name, app_landstatus.landstatus_name, app_estatetype.estatetype_name, app_estatefunction.estatefunction_name ORDER BY app_estate.estate_id ASC

SELECT app_estate.estate_id, app_estate.estate_name, app_estate.estate_floor, app_estate.estate_code, MAX(app_district.district_name) AS district_name, app_estate.estate_address, app_estate.estate_area_land, app_estate.estate_area_landcertificate, app_estate.estate_area_estate, app_estate.estate_area_estatecertificate, app_yjqy.yjqy_name, app_qsdw.qsdw_name, app_landstatus.landstatus_name, app_estatetype.estatetype_name, app_estatefunction.estatefunction_name, SUM(CASE WHEN app_suite.suite_isselfuse = 0 THEN app_suite.suite_area ELSE NULL END) AS rent_area, SUM(CASE WHEN app_suite.suite_isselfuse = 1 THEN app_suite.suite_area ELSE NULL END) AS selfuse_area, (app_estate.estate_area_estate - SUM(app_suite.suite_area)) AS vacant_area, COUNT(app_suite.suite_id) AS suite_num FROM app_estate LEFT JOIN app_district ON app_estate.estate_district_code = app_district.district_code LEFT JOIN app_yjqy ON app_estate.yjqy_id = app_yjqy.yjqy_id LEFT JOIN app_qsdw ON app_estate.qsdw_id = app_qsdw.qsdw_id LEFT JOIN app_estatefunction ON app_estate.estate_estatefunction_id = app_estatefunction.estatefunction_id LEFT JOIN app_estatetype ON app_estate.estate_estatetype_id = app_estatetype.estatetype_id LEFT JOIN app_landstatus ON app_estate.estate_landstatus_id = app_landstatus.landstatus_id JOIN app_suite ON app_suite.estate_id = app_estate.estate_id
app_estate.estate_id != 0
AND app_estate.estate_name LIKE #{estateNameLike}
AND app_estate.estate_code LIKE #{estateCodeLike}
AND app_district.district_name LIKE #{districtNameLike}
AND app_yjqy.yjqy_name = #{query.yjqyName}
